INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Caderi de pietre', 'În urma unor ploi torențiale, au avut loc caderi de pietre pe versanții stâncoși din zona montană. Procesul a durat aproximativ 10 zile, cu factori de declanșare precum saturarea solului și eroziunea accentuată. Evenimentul a afectat versanți importanți din regiune.', 10, 'Eroziune, Ploi torențiale', 1);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Avalanse', 'Sezonul de iarnă a fost caracterizat de acumularea unei cantități semnificative de zăpadă pe versanții înclinați din regiunea alpină. O avalanșă s-a declanșat după o perioadă de topire bruscă a zăpezii, având un impact asupra văilor adiacente. Procesul a durat aproximativ 15 minute, dar a avut consecințe semnificative.', 15, 'Topirea zăpezii, Inclinația terenului', 2);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Creep', 'Deplasarea lentă a solului a fost observată într-o zonă umedă cu teren înclinat. Procesul a avut o durată de aproximativ 20 de ani și a fost cauzat de ploile abundente și de umiditatea crescută a solului. Factorii de declanșare au inclus și activitatea seismică sporadică în regiune.', 20, 'Inclinarea terenului, Umiditate', 3);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Caderi de pietre', 'Caderi de pietre au avut loc pe versanții afectați de eroziune și de activitățile umane. Procesul a durat aproximativ 12 zile și a fost amplificat de lucrările de construcție în apropiere. Factorii de declanșare au inclus precipitațiile abundente și instabilitatea terenului.', 12, 'Eroziune, Activitate umană', 1);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Avalanse', 'O avalanșă s-a produs în urma unei schimbări rapide a condițiilor meteorologice, cu temperaturi în creștere și topire accelerată a zăpezii. Durata procesului a fost de aproximativ 18 minute, iar factorii de declanșare au inclus topirea bruscă a stratului de zăpadă și instabilitatea terenului.', 18, 'Topirea zăpezii, Instabilitate teren', 2);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Caderi de pietre', 'Lucrări de consolidare au fost efectuate pentru a preveni caderile de pietre pe versanții instabili. Procesul a avut o durată de aproximativ 8 luni și a implicat implementarea de plase de protecție și baraje. Factorii de declanșare au inclus eroziunea continuă și instabilitatea versanților.', 8, 'Lucrări de consolidare, Monitorizare', 1);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Avalanse', 'O alertă de avalanșă a fost emisă în urma condițiilor meteorologice nefavorabile. Evacuarea preventivă s-a desfășurat pe o perioadă de 22 de ore, implicând sisteme avansate de avertizare și comunicare rapidă. Factorii de declanșare au inclus topirea rapidă a zăpezii și instabilitatea terenului.', 22, 'Alertă meteo, Topirea zăpezii', 2);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Creep', 'Deplasarea lentă a solului a fost monitorizată constant cu ajutorul senzorilor și analizelor periodice. Procesul a avut o durată de aproximativ 30 de ani, iar măsurile de prevenire au inclus injecții de consolidare și lucrări de stabilizare urgentă. Factorii de declanșare au inclus umiditatea crescută și variațiile înclinației terenului.', 30, 'Senzori, Analize periodice', 3);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Caderi de pietre', 'Un risc crescut de caderi de pietre a fost identificat pe versanții expuși. Studiul asupra impactului caderilor de pietre a implicat analize geotehnice și evaluarea daunelor cauzate infrastructurii locale. Procesul a durat aproximativ 14 zile, cu factori de declanșare precum eroziunea accelerată și activitatea umană intensă.', 14, 'Analiză geotehnică, Evaluare daune', 1);
