INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Creep', 'Strategii de adaptare la schimbările de sol prin monitorizare continuă și planificare pe termen lung. Procesul a durat aproximativ 27 de ani și a implicat implementarea de strategii de consolidare și reglementări ale construcțiilor. Factorii de declanșare au inclus variațiile înclinației terenului și umiditatea crescută.', 27, 'Monitorizare continuă, Planificare pe termen lung', 3);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Caderi de pietre', 'Evaluare a vulnerabilității la caderi de pietre, inclusiv analize de risc și identificarea punctelor critice. Procesul a durat aproximativ 14 luni, iar măsurile de prevenire au inclus consolidarea versanților și instalarea de plase de protecție. Factorii de declanșare au inclus eroziunea accelerată și activitatea umană intensă.', 14, 'Analize de risc, Identificare puncte critice', 1);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Avalanse', 'Implementare tehnologii de detectare a riscului de avalanse, inclusiv utilizarea de senzori și teledetecție. Procesul a avut o durată de aproximativ 18 luni, iar măsurile de protecție au inclus sisteme de avertizare timpurie și planuri de evacuare. Factorii de declanșare au inclus topirea rapidă a zăpezii și instabilitatea terenului.', 18, 'Senzori, Teledetecție', 2);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Creep', 'Stabilizare terenuri vulnerabile la deplasări prin utilizarea de injecții de consolidare și lucrări de terasare. Procesul a durat aproximativ 31 de ani și a implicat monitorizare continuă și analize periodice ale solului. Factorii de declanșare au inclus variațiile înclinației terenului și umiditatea crescută.', 31, 'Injecții de consolidare, Lucrări de terasare', 3);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Caderi de pietre', 'Planuri de intervenție în caz de caderi de pietre, inclusiv echipamente de urgență și rute de evacuare. Procesul a durat aproximativ 10 luni, iar măsurile de prevenire au inclus pregătirea comunității prin instruiri și instalarea de echipamente de urgență. Factorii de declanșare au inclus eroziunea accelerată și activitatea umană intensă.', 10, 'Echipamente de urgență, Rute de evacuare', 1);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Avalanse', 'Simulări pentru evaluarea riscului de avalanse, inclusiv modelare computerizată și analize statistice. Procesul a durat aproximativ 16 luni, iar măsurile de protecție au inclus implementarea de sisteme avansate de avertizare și teledetecție. Factorii de declanșare au inclus topirea rapidă a zăpezii și instabilitatea terenului.', 16, 'Modelare computerizată, Analize statistice', 2);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Creep', 'Restaurare habitat natural după deplasările de sol, inclusiv revegetare și protecția biodiversității. Procesul a durat aproximativ 29 de ani și a implicat reîmpădurire și refacere a ecosistemului. Factorii de declanșare au inclus variațiile înclinației terenului și umiditatea crescută.', 29, 'Reabilitare naturală, Refacere ecosistem', 3);
INSERT INTO procese_geomorfologice (nume_proces, descriere_proces, durata_proces, factori, dendrogeomorfologie_id) VALUES('Caderi de pietre', 'Implementare de tehnologii avansate pentru prevenirea caderilor, inclusiv sisteme de avertizare și monitorizare geotehnică. Procesul a durat aproximativ 15 luni și a implicat implementarea de tehnologii avansate pentru prevenirea caderilor. Factorii de declanșare au inclus eroziunea continuă și activitatea umană intensă.', 15, 'Sisteme de avertizare, Monitorizare geotehnică', 1);
